import Header from "../../components/header";
import "./style.scss";
import {useEffect, useState} from "react";
import api from "../../api/axios/axios";
import Dish from "../../components/dish";

export default function Shop(){
  const [shops, setShops] = useState([]);
  const [dishes, setDishes] = useState([]);
  const [selectedShop, setSelectedShop] = useState(shops[0]);

  useEffect(() => {
    const getAllShops = async () => {
      try{
        const response = await api.get('/api/shop');
        setShops(response.data);
      }catch (e) {
        if (e.response) {
          console.log(e.response.data);
          console.log(e.response.status);
          console.log(e.response.headers);
        }else{
          console.log(`Error ${e.message}`);
        }
      }
    }
    getAllShops();
  }, []);

  useEffect(() => {
    async function getDishesByShop() {
      try {
        const response = await api.get('/api/dish?shopId=' + selectedShop.id);
        return response.data;
      }catch (e) {
        if (e.response) {
          console.log(e.response.data);
          console.log(e.response.status);
          console.log(e.response.headers);
        }else{
          console.log(`Error ${e.message}`);
        }
      }
    }

    setDishes(getDishesByShop(selectedShop));
  }, [selectedShop]);

  return (
    <div>
      <Header/>
      <div className="container">
        <div className="shops">
          <ul>
            <h3>Shops:</h3>
          {
            shops && shops.map((i) =>
            <li>
              <button key={i.id} onClick={() => setSelectedShop(document.querySelector())}>{i.title}</button>
            </li>
            )
          }
          </ul>
        </div>
        <div className="menu">
          {
            //dishes && selectedShop ? dishes.map((i) => (<Dish title={i.title} price={i.price} img={i.img}/>)) : <h2>This menu is empty</h2>
          }
        </div>
      </div>
    </div>
  );
}
