import Header from "../../components/header";
import "./style.scss";
import {useEffect, useState} from "react";
import axios from "../../api/axios/axios";
import Dish from "../../components/dish";

export default function Shop(){
  const {shops, setShops} = useState({});
  const {dishes, setDishes} = useState({});
  const {selectedShop, setSelectedShop} = useState({});

  const getDishesByShop = async function(shop){
    const shopId = shops.find((i) => {
      if(i.title === shop)
        return i.shopId;
    });
    return await axios.get('api/dish/' + shopId);
  }

  useEffect(() => {
    async function getAllShops(){
      const data = await axios.get('api/shop');
      setShops(data);
    }

    getAllShops();
  }, []);

  useEffect(() => {
    setDishes( getDishesByShop(selectedShop));
  }, [selectedShop]);

  return (
    <div>
      <Header/>
      <div className="container">
        <div className="shops">
          <ul>
          {shops && shops.map((i) =>
            <li>
              <button onClick={() => setSelectedShop(document.querySelector("button").title)}>{i.title}</button>
            </li>
          )}
          </ul>
        </div>
        <div className="menu">
          {dishes && dishes.map((i) => (
            <Dish title={i.title} price={i.price} img={i.img}/>
          ))}
        </div>
      </div>
    </div>
  );
}
