import Header from "../../components/header";
import "./style.scss";
import {useEffect, useState} from "react";
import axios from "../../api/axios/axios";
import Dish from "../../components/dish";

export default function Shop(){
  const [shops, setShops] = useState([]);
  const [dishes, setDishes] = useState([]);
  const [selectedShop, setSelectedShop] = useState([]);

  useEffect(() => {
    async function getAllShops(){
      const response = await axios.get('/api/shop');
      console.log("Shops: ");
      response.data.map((i) => console.log(i.data));
      return response.data;
    }

    setShops(getAllShops());
  }, []);

  useEffect(() => {
    async function getDishesByShop(shop) {
      const shopId = shops.find((i) => {
        if (i.title === shop)
          return i.shopId;
      });
      const response = await axios.get('/api/dish/id:' + shopId);
      console.log("Dishes" + response.data);
      return response.data;
    }

    setDishes(getDishesByShop(selectedShop));
  }, [selectedShop]);

  return (
    <div>
      <Header/>
      <div className="container">
        <div className="shops">
          <ul>
          {
            shops && shops.map((i) =>
            <li>
              <button onClick={() => setSelectedShop(document.querySelector("button").title)}>{i.title}</button>
            </li>
          )}
          </ul>
        </div>
        <div className="menu">
          {dishes && dishes.map((i) => (
            <Dish title={i.title} price={i.price} img={i.img}/>
          ))}
        </div>
      </div>
    </div>
  );
}
