import Header from "../../components/header";
import {useEffect, useState} from "react";
import axios from "../../api/axios/axios";

export default function Shop(){
  const {shops, setShops} = useState({});
  const {dishes, setDishes} = useState({});
  const {selectedShop, setSelectedShop} = useState({});

  const getAllShops = async function(){
    return await axios.get('api/shop');
  }

  const getDishesByShop = async function(shopId){
    return await axios.get('api/dish/' + shopId);
  }

  useEffect(() => {
    setShops( getAllShops());
  }, []);

  useEffect(() => {
    setShops( getDishesByShop());
  }, []);

  const buttonHandler = () => {
    setSelectedShop(document.querySelector("button"));
  };

  return (
    <div>
      <Header/>
      <div className="container">
        <div className="shops">
          <ul>
          {shops && shops.map((i) =>
            <li>
              <button onClick={buttonHandler}>{i.title}</button>
            </li>
          )}
          </ul>
        </div>
        <div className="menu">
        </div>
      </div>
    </div>
  );
}
