import Header from "../../components/header";
import "./style.scss";
import {useEffect, useState} from "react";
import api from "../../api/axios/axios";
import Dish from "../../components/dish";

export default function Shop(){
  const [shops, setShops] = useState([]);
  const [dishes, setDishes] = useState([{
    title: "gsdfgs",
    price: 222,
    img: "hdshsdh"
  }]);
  const [selectedShop, setSelectedShop] = useState([]);

  useEffect(() => {
    const getAllShops = async () => {
      try{
        const response = await api.get('/api/shop');
        setShops(response.data);
      }catch (e) {
        if (e.response) {
          console.log(e.response.data.message);
          console.log(e.response.status);
          console.log(e.response.headers);
        }else{
          console.log(`Error ${e.message}`);
        }
      }
    }
    getAllShops();
  }, []);

  useEffect(() => {
    async function getDishesByShop(shop) {
      const shopId = shops.find((i) => {
        if (i.title === shop)
          return i.shopId;
      });
      const response = await api.get('/api/dish/id:' + shopId);
      return response.data;
    }

    setDishes(getDishesByShop(selectedShop));
  }, [selectedShop]);

  return (
    <div>
      <Header/>
      <div className="container">
        <div className="shops">
          <ul>
            <h3>Shops:</h3>
          {
            shops && shops.map((i) =>
            <li key={i.id}>
              <button onClick={() => setSelectedShop(dishes[i].title)}>{i.title}</button>
            </li>
            )
          }
          </ul>
        </div>
        <div className="menu">
          {
            dishes && selectedShop ? dishes.map((i) => (<Dish title={i.title} price={i.price} img={i.img}/>)) : <h2>This menu is empty</h2>
          }
        </div>
      </div>
    </div>
  );
}
