import "./style.scss"
import {useContext} from "@types/react";
import {CartDishesContext} from "../cartDishesContext";

export default function Dish({title, price, img}){
  const [cartDishes, setCartDishes] = useContext(CartDishesContext);

  return(
    <div className="dish">
      <img src={`./static/${img}`} alt={title} height="150px" width="230px"/>
      <div className="dish_info">
        <span>
          <h3>{title}</h3>
          <p>{price} uah</p>
        </span>
        <button className="add_button" onClick={setCartDishes()}>Add</button>
      </div>

    </div>
  );
}
